
public class Operators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int value=1+2;
		System.out.println("1+2=" +value);
		int Answer=value;
		
		value=value-1;
		System.out.println(Answer + "-1=" +value);
		Answer=value;
		
		value= value*2;
		System.out.println(Answer + "*2=" +value);
		Answer=value;
		
		value=value/2;
		System.out.println(Answer + "/2=" +value);
		Answer=value;
		
		value=value+8;
		System.out.println(Answer + "+8=" +value);
		Answer=value;
		
		value=value%7;
		System.out.println(Answer + "%7=" +value);
		Answer=value;
		
	}
	}


