
public class UnaryoperatorDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int value=+1;		
		System.out.println(value);
		 value--;
		 System.out.println(value);
		 value++;
		 System.out.println(value);

		 value=-value;
		 System.out.println(value);
		 
		 boolean Answer=true;
		 System.out.println(Answer);
		 System.out.println(!Answer);
		 

		 
	
	}

}
