package Looping;
//TraingleNumberPattern
//1
//21
//321
//4321
//54321

public class IncreaseNumbertrainglePattern {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		     for(int row=1;row<=5;row++)
				{
				for(int i=row; i>=1;i--)
					{
					System.out.print(i);
					}
					System.out.println();
				 }
	}

}
